) values (
#{value.id}, #{value.sessionId}, #{value.userId}, #{value.category}, #{value.type}, #{value.url}, #{value.organization}, #{value.name}, #{value.title}, #{value.email}, #{value.phone}, #{value.siteName}, #{value.requests}, #{value.responses}, #{value.handled}, #{value.dateHandled}, #{value.taxFileId}, #{value.advancedQty}, #{value.standardQty}, #{value.webhardQty}, #{value.viewerQty}, #{value.projectQty}, #{value.changeQty}, #{value.manufacturingQty}, #{value.qualityQty}, #{value.rmsQty}, #{value.testQty}, #{value.contractType}, #{value.privacyAgreed}, #{value.prAgreed}, #{value.product}, #{value.benefit}, #{value.businessType}, #{value.date},
